INSERT INTO `tabPrint Format` VALUES ('Advance Payment Entry', '2018-08-10 16:51:30.986010', '2018-08-16 08:58:47.864498', 'Administrator', 'Administrator', 0, NULL, NULL, NULL, 0, 'Accounts', 0, 1, 'Default', NULL, '<style> \n.print-format table, .print-format tr, \n.print-format td, .print-format div, .print-format p {\n	font-family: Arial;\n	line-height: 170%;\n	vertical-align: middle;\n}\n@media screen {\n	.print-format {\n	\n	padding: 0.05in !important;\n	min-height: auto;\n	}\n	.print-format th {\n	background-color: black !important;\n	border-bottom: 0px !important;\n	color: white !important;\n	}\n	\n	.table {\n	margin: 10px 0px !important;\n	}	\n}\n.print-preview {\n	padding: 0px;\n	margin: auto;\n	min-height: auto !important;\n}\n</style>\n{% set companydoc = frappe.get_doc(\"Company\", doc.company) %}\n{% set Customerdoc = frappe.get_doc(\"Customer\", doc.customer) %}\n{% set Paymententry = frappe.get_doc(\"Payment Entry\", doc.payment)%}\n\n<div style=\"border: 2px solid;height: auto;float: left; width:100% \">\n	{% if companydoc.pos_logo != None %}\n	<div style=\"float: left;width: 20%;\">\n	<p>\n	\n	<img src={{ companydoc.pos_logo }} style=\"max-height: 100px;margin-top: 15%;width: 120px;margin-left: 5px;\"><br>\n	\n	</p>\n	</div>\n	<div style=\"float: left;width: 48%;border-right: 2px solid;height: 188px;\">\n	<p style=\"font-size: 12px;margin-top: 15px;\">\n	<b style=\" font-size: 20px;\">{{ doc.company }}</b><br>{{ doc.company_pos_address }}</p>\n	{% if companydoc.vat_number != None %}\n	<p style=\"font-size: 12px;\">TRN # {{ companydoc.vat_number }}</p>\n	{% endif %}	\n	<br/>\n	</div>\n{% else %}\n	<div style=\"float: left;width: 68%;border-right: 2px solid;height: auto;\">\n	<p style=\"font-size: 12px;margin-top: 15px;margin-left: 10px;\">\n	<b style=\" font-size: 20px;\">{{ doc.company }}</b><br>{{ doc.company_pos_address }}</p>\n	<p style=\"font-size: 12px;margin-left: 10px;\">TRN # {{ companydoc.vat_number }}</p>\n	<br/>\n	</div>\n{% endif %}\n	<div style=\"float: right;width: 32%;\">\n	<div style=\"border-bottom: 2px solid;\">\n	{% if doc.payment_type == \'Receive\' %}\n	<p style=\"font-size: 20px;font-weight: bold;margin-left: 10px;\">RECEIVE ENTRY</p>\n	{% else %}\n	<p style=\"font-size: 20px;font-weight: bold;margin-left: 10px;\">PAYMENT ENTRY</p>\n	{% endif %}\n	</div>\n	<p style=\"font-size: 12px;margin-left: 10px;\">\n	{% if doc.payment_type == \'Receive\' %}\n	<b>{{ _(\"Receive Number\") }}:</b>{{ doc.name }} <br>\n	{% else %}\n	<b>{{ _(\"Payment Number\") }}:</b>{{ doc.name }} <br>\n	{% endif %}\n	<b>{{ _(\"Date\") }}:</b>{{ doc.posting_date }} <br>\n	<b>{{ _(\"Mode Of Payment\") }}:</b>{{ doc.mode_of_payment }} <br>\n	<b>{{ _(\"Served By\") }}:</b>{{ doc.served_by }}<br>\n	{% if doc.reference_no != None and doc.reference_no != \"\" %}\n	<b>{{ _(\"Cheque No\") }}:</b>{{ doc.reference_no }} <br>\n	{% endif %}\n	{% if doc.drawn_on != None and doc.drawn_on != \"\" %}\n	<b>{{ _(\"Bank Name\") }}:</b> {{ doc.drawn_on }}<br>\n	{% endif %}\n	{% if doc.reference_date != None and doc.reference_date != \"\" %}\n	<b>{{ _(\"Cheque Date\") }}:{{ doc.reference_date }}</b> \n	{% endif %}\n	\n	</p>\n	</div>\n	</div>\n	<div style=\"border-bottom: 2px solid;border-left: 2px solid;border-right: 2px solid;height: auto;width: 100%;float: left;margin-bottom: 10px;\">\n	<div style=\"float: left; width: 45%\">\n	<p style=\"font-size: 12px;margin-left: 3%;\">\n	<b>{{ _(\"Bill To\") }}:</b> <b>{{ doc.party_name }}</b>\n	{% if doc.shipping_address != None and doc.shipping_address != \"\" %}\n	<br/><b>{{ _(\"Address\") }}:</b> {{ doc.shipping_address }}\n	{% endif %}\n	{% if Customerdoc.mobile_number != None and Customerdoc.mobile_number != \"\" %}\n	<br/><b>{{ _(\"Mobile\") }}:</b> {{ Customerdoc.mobile_number }}\n	{% endif %}\n	\n	\n	</p>\n	</div>\n	</div>\n	<br>\n	<br>\n{% if doc.mode_of_payment == \'Cheque\' %}\n	<table style=\"width:100%\">\n\n  <tr>\n    <td>Cheque/Reference No</td>\n    <td><b>{{ doc.reference_no }}</b></td>\n    <td>Cheque/Reference Date</td>\n    <td><b>{{ doc.reference_date }}</b></td>\n  </tr>\n</table>\n{%- endif -%}\n<br>\n<table style=\"width:100%\">\n\n  <tr>\n    <td>Paid Amount</td>\n    <td><b>{{ doc.paid_amount }}</b></td>\n</tr>\n	<br>\n    <td>Remarks</td>\n    <td><b>{{ frappe.utils.money_in_words(doc.paid_amount) }}</b></td>\n  </tr>\n</table>\n<br>\n<br>\n<br>\n	<table style=\"width:100%\">\n  <tr>\n    <td style=\"text-align: center;\">______________</td>\n    <td style=\"text-align: center;\">______________</td>\n  </tr>\n  <tr>\n    <td style=\"text-align: center;\">Prepared By</td>\n    <td style=\"text-align: center;\">Recived By</td>\n  </tr>\n</table>\n<hr>\n	<p>{{ doc.terms or \"\" }}</p>\n<p class=\"text-center\" style=\"font-size:11px\"><b>{{ _(\"Thank you, please visit again.\") }}</b></p>\n\n<p class=\"text-center\" style=\"font-size:11px\"><b>{{ _(\"Powered by: www.uaepos.com\") }}</b></p>\n<script>\n	<script>\n	var currentdate = new Date(); \n	var datetime = currentdate.getDate() + \"/\"\n	+ (currentdate.getMonth()+1) + \"/\" \n	+ currentdate.getFullYear() + \" \" \n	+ currentdate.getHours() + \":\" \n	+ currentdate.getMinutes() + \":\" \n	+ currentdate.getSeconds();\n\n	$(\'.curr_date_time\').text(datetime);\n\n	</script>', NULL, NULL, NULL, 0, 'No', 0, NULL, 'Server', NULL, 'Payment Entry', 0, 0);
INSERT INTO `tabPrint Format` VALUES ('Cheque Printing Format', '2012-04-11 13:16:56.000000', '2015-05-29 01:57:51.203850', 'Administrator', 'Administrator', 0, NULL, NULL, NULL, 1, NULL, 0, 1, NULL, NULL, '<div style=\"position: relative\">\n\n	{%- from \"templates/print_formats/standard_macros.html\" import add_header -%}\n<div class=\"page-break\">\n    {%- if not doc.get(\"print_heading\") and not doc.get(\"select_print_heading\") \n        and doc.set(\"select_print_heading\", _(\"Payment Advice\")) -%}{%- endif -%}\n    {{ add_header(0, 1, doc, letter_head, no_letterhead) }}\n\n{%- for label, value in (\n        (_(\"Voucher Date\"), frappe.utils.formatdate(doc.voucher_date)),\n        (_(\"Reference / Cheque No.\"), doc.cheque_no),\n        (_(\"Reference / Cheque Date\"), frappe.utils.formatdate(doc.cheque_date))\n    ) -%}\n    <div class=\"row\">\n        <div class=\"col-xs-4\"><label class=\"text-right\">{{ label }}</label></div>\n        <div class=\"col-xs-8\">{{ value }}</div>\n    </div>\n{%- endfor -%}\n	<hr>\n	<p>{{ _(\"This amount is in full / part settlement of the listed bills\") }}:</p>\n{%- for label, value in (\n         (_(\"Amount\"), \"<strong>\" + doc.get_formatted(\"total_amount\") + \"</strong><br>\" + (doc.total_amount_in_words or \"\") + \"<br>\"),\n        (_(\"References\"), doc.remark)\n    ) -%}\n    <div class=\"row\">\n        <div class=\"col-xs-4\"><label class=\"text-right\">{{ label }}</label></div>\n        <div class=\"col-xs-8\">{{ value }}</div>\n    </div>\n    {%- endfor -%}\n    <hr>\n	<div style=\"position: absolute; top: 14cm; left: 0cm;\">\n		Prepared By</div>\n	<div style=\"position: absolute; top: 14cm; left: 5.5cm;\">\n		Authorised Signatory</div>\n	<div style=\"position: absolute; top: 14cm; left: 11cm;\">\n		Received Payment as Above</div>\n	<div style=\"position: absolute; top: 16.4cm; left: 5.9cm;\">\n		<strong>_____________</strong></div>\n	<div style=\"position: absolute; top: 16.7cm; left: 6cm;\">\n		<strong>A/C Payee</strong></div>\n	<div style=\"position: absolute; top: 16.7cm; left: 5.9cm;\">\n		<strong>_____________</strong></div>\n	<div style=\"position: absolute; top: 16.9cm; left: 12cm;\">\n		{{ frappe.utils.formatdate(doc.cheque_date) }}</div>\n	<div style=\"position: absolute; top: 17.9cm; left: 1cm;\">\n		{{ doc.pay_to_recd_from }}</div>\n	<div style=\"position: absolute; top: 18.6cm; left: 1cm; width: 7cm;\">\n		{{ doc.total_amount_in_words }}</div>\n	<div style=\"position: absolute; top: 19.7cm; left: 12cm;\">\n		{{ doc.get_formatted(\"total_amount\") }}</div>\n</div>', NULL, NULL, NULL, 0, 'Yes', 0, NULL, 'Server', NULL, 'Journal Entry', 0, 0);
INSERT INTO `tabPrint Format` VALUES ('Consultation Print', '2017-04-10 14:05:53.355863', '2017-05-15 16:36:06.034909', 'Administrator', 'Administrator', 0, NULL, NULL, NULL, 0, 'Healthcare', 0, 1, 'Default', NULL, '<div >\n    {% if letter_head and not no_letterhead -%}\n    <div class=\"letter-head\">{{ letter_head }}</div>\n    <hr>\n    {% else %}\n    <div align=\"right\">\n      <h1>{{doc.name}}</h1>\n    </div>\n    {%- endif %}\n    <div class=\"row section-break\">\n        <div class=\"col-xs-6 column-break\">\n                {% if doc.appointment %}\n	        <div class=\"row\">\n			<div class=\"col-xs-4 text-left\">\n			<label>Appointment</label>\n			</div>\n			<div class=\"col-xs-7  value\">\n			<strong>: </strong>{{doc.appointment}}\n			</div>\n		</div>\n		{%- endif -%}\n\n                <div class=\"row\">\n		      <div class=\"col-xs-4 text-left\">\n			 <label>Patient</label>\n		      </div>\n                     {% if doc.patient %}\n		      <div class=\"col-xs-7  value\">\n			  <strong>: </strong>{{doc.patient}}\n		      </div>\n                     {% else %}\n                     <div class=\"col-xs-7  value\">\n			 <strong>: </strong><em>Patient Name</em>\n		     </div>\n                     {%- endif -%}\n		</div>\n	        <div class=\"row\">\n			<div class=\"col-xs-4 text-left\">\n				<label>Age</label>\n			</div>\n			<div class=\"col-xs-7  value\">\n			     <strong>: </strong> {{doc.patient_age}}\n			</div>\n		</div>\n\n                <div class=\"row\">\n                        <div class=\"col-xs-4 text-left\">\n				<label>Gender</label>\n			</div>\n			<div class=\"col-xs-7  value\">\n			     <strong>: </strong> {{doc.patient_sex}}\n			</div>\n                </div>\n\n        </div>\n        <div class=\"col-xs-6 column-break\">\n\n          <div class=\"row\">\n	      <div class=\"col-xs-4 text-left\">\n		    <label>Doctor</label>\n	      </div>\n              {% if doc.physician %}\n	      <div class=\"col-xs-7  text-left value\">\n			<strong>: </strong>{{doc.physician}}\n	      </div>\n              {%- endif -%}\n	</div>\n\n          {% if doc.consultation_date %}\n	  <div class=\"row\">\n		<div class=\"col-xs-4 text-left\">\n		<label>Date</label>\n		</div>\n		<div class=\"col-xs-7 text-left value\">\n		<strong>: </strong>{{doc.consultation_date}}\n		</div>\n          </div>\n	  {%- endif -%}\n          {% if doc.consultation_time %}\n	  <div class=\"row\">\n		<div class=\"col-xs-4 text-left\">\n		<label>Time</label>\n		</div>\n		<div class=\"col-xs-7 text-left value\">\n		<strong>: </strong>{{doc.consultation_time}}\n		</div>\n          </div>\n	  {%- endif -%}\n          {% if doc.visit_department %}\n	  <div class=\"row\">\n		<div class=\"col-xs-4 text-left\">\n		<label>Department</label>\n		</div>\n		<div class=\"col-xs-7 text-left value\">\n		<strong>: </strong>{{doc.visit_department}}\n		</div>\n          </div>\n          {%- endif -%}\n        </div>\n\n   </div>\n\n</div>\n<div>\n     <hr>\n     {% if doc.symptoms_in_print%}\n       {% if doc.symptoms %}\n         Complaints:\n         <strong>{{doc.symptoms}}</strong>\n  	   <br>\n       {%- endif -%}\n     {%- endif -%}\n\n     {% if doc.diagnosis_in_print%}\n       {% if doc.diagnosis %}\n  	     Diagnosis:\n         <strong>{{doc.diagnosis}}</strong>\n         <br>\n       {%- endif -%}\n     {%- endif -%}\n\n</div>\n\n<div>\n      {% if doc.drug_prescription %}\n      <br>\n      Rx,\n      <table class=\"table\">\n        <tbody>\n            <!--<tr>\n                <th>Drug</th>\n                <th class=\"text-left\">Dosage</th>\n                <th class=\"text-left\">Period</th>\n                <th>Remark</th>\n            </tr>-->\n\n            {%- for row in doc.drug_prescription -%}\n            <tr>\n              <td style=\"width: 30%;border:none;\">\n                {%- if row.drug_name -%}<b>{{ row.drug_name }}</b>{%- endif -%}\n              </td>\n            	<td style=\"width: 20%;text-align: left;border:none;\">\n                {%- if row.dosage -%}{{ row.dosage }}{%- endif -%}\n              </td>\n            	<td style=\"width: 20%;text-align: left;border:none;\">\n                {%- if row.period -%}{{ row.period }}{%- endif -%}\n		          </td>\n              <td style=\"width: 30%;text-align: left;border:none;\">\n			             <div style=\"border: 0px;\">\n                      {%- if row.comment -%}{{ row.comment }}{%- endif -%}\n                   </div>\n		           </td>\n            </tr>\n	          {%- endfor -%}\n       </tbody>\n      </table>\n\n\n      {%- endif -%}\n</div>\n\n\n<div>\n      {% if doc.test_prescription %}\n      Investigations,\n      <table class=\"table\">\n        <tbody>\n            <!--<tr>\n                <th>Test</th>\n                <th>Remark</th>\n            </tr>-->\n\n            {%- for row in doc.test_prescription -%}\n            <tr>\n              <td style=\"width: 30%;border:none;\">\n                {%- if row.test_name -%}<b>{{ row.test_name }}</b>{%- endif -%}\n              </td>\n              <td style=\"width: 30%;text-align: left;border:none;\">\n			           <div style=\"border: 0px;\">\n                   {%- if row.test_comment -%}{{ row.test_comment }}{%- endif -%}\n                 </div>\n		          </td>\n             </tr>\n\n	           {%- endfor -%}\n       </tbody>\n      </table>\n\n\n      {%- endif -%}\n</div>\n<div>\n    {% if doc.consultation_comment %}\n      <br>\n      {{doc.consultation_comment}}\n     {%- endif -%}\n</div>\n', NULL, NULL, NULL, 0, 'Yes', 0, NULL, 'Server', NULL, 'Consultation', 0, 0);
INSERT INTO `tabPrint Format` VALUES ('Credit Note', '2014-08-28 11:11:39.796473', '2015-07-22 17:42:01.560817', 'Administrator', 'Administrator', 0, 'Journal Entry', '__print_formats', 'DocType', 2, NULL, 0, 0, NULL, NULL, '{%- from \"templates/print_formats/standard_macros.html\" import add_header -%}\n\n<div class=\"page-break\">\n    {%- if not doc.get(\"print_heading\") and not doc.get(\"select_print_heading\") \n        and doc.set(\"select_print_heading\", _(\"Credit Note\")) -%}{%- endif -%}\n    {{ add_header(0, 1, doc, letter_head, no_letterhead) }}\n\n    {%- for label, value in (\n        (_(\"Credit To\"), doc.pay_to_recd_from),\n        (_(\"Date\"), frappe.utils.formatdate(doc.voucher_date)),\n        (_(\"Amount\"), \"<strong>\" + doc.get_formatted(\"total_amount\") + \"</strong><br>\" + (doc.total_amount_in_words or \"\") + \"<br>\"),\n        (_(\"Remarks\"), doc.remark)\n    ) -%}\n\n    <div class=\"row\">\n        <div class=\"col-xs-3\"><label class=\"text-right\">{{ label }}</label></div>\n        <div class=\"col-xs-9\">{{ value }}</div>\n    </div>\n\n    {%- endfor -%}\n\n    <hr>\n    <br>\n    <p class=\"strong\">\n        {{ _(\"For\") }} {{ doc.company }},<br>\n        <br>\n        <br>\n        <br>\n        {{ _(\"Authorized Signatory\") }}\n    </p>\n</div>\n\n\n', NULL, NULL, NULL, 0, 'Yes', 0, NULL, 'Server', NULL, 'Journal Entry', 0, 0);
INSERT INTO `tabPrint Format` VALUES ('Custom Format', '2018-03-21 15:52:04.800500', '2018-03-31 11:03:19.071271', 'Administrator', 'Administrator', 0, NULL, NULL, NULL, 0, 'Accounts', 1, 1, 'Default', NULL, '<style> \n.print-format table, .print-format tr, \n.print-format td, .print-format div, .print-format p {\n	font-family: Arial;\n	line-height: 170%;\n	vertical-align: middle;\n}\n@media print {\n	.print-format {\n		\n		padding: 0.05in !important;\n		min-height: auto;\n	}\n	.print-format th {\n		background-color: black !important;\n		border-bottom: 0px !important;\n		color: white !important;\n	}\n	\n	.table {\n		margin: 10px 0px !important;\n	}	\n}\n.print-preview {\n	padding: 0px;\n	margin: auto;\n	min-height: auto !important;\n}\n</style>\n{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}\n    {% if letter_head and not no_letterhead %}\n    <div class=\"letter-head\">{{ letter_head }}</div>\n    {% endif %}\n    \n    {% if max_pages > 1 %}\n    <p class=\"text-right\">{{ _(\"Page #{0} of {1}\").format(page_num, max_pages) }}</p>\n    {% endif %}\n{%- endmacro -%}\n{{ add_header(0,1,doc,letter_head, no_letterhead) }}\n<div class=\"print-heading\">\n	<h2>\n		TAX INVOICE<br>\n		<small>{{ doc.name }}</small>\n	</h2>\n</div>\n<h2 class=\"text-center\">\n	{% if doc.invoice_copy -%}\n		<small>{{ doc.invoice_copy }}</small>\n	{% endif -%}\n</h2>\n<br>\n<div style=\"float: right;width: 50%;\">\n			<p style=\"font-size: 12px;\">\n				<b>{{ _(\"Invoice Date\") }}:</b> {{ doc.get_formatted(\"posting_date\") }} {{ doc.get_formatted(\"posting_time\") }}<br>\n					<b>{{ _(\"Payment Due Date\") }}:</b> {{ doc.get_formatted(\"due_date\") }} {{ doc.get_formatted(\"posting_time\") }}<br>			\n			</p>\n		</div>\n<div style=\"float: right;width: 50%;\">\n			<p style=\"font-size: 12px;\">\n				<b>{{ _(\"Payment Status\") }}:</b> {{ doc.status }}<br>\n				<b>{{ _(\"Print Date\") }}:</b> {{ frappe.utils.formatdate(frappe.utils.today(), \'dd-MM-YYYY\') }} 																					{{ frappe.utils.now_datetime().strftime(\'%I:%M %p\') }}\n								\n			</p>\n		</div>\n\n<div style=\"float: left; width: 50%\">\n<hr/>\n			<p style=\"font-size: 12px;\">\n				<b>{{ _(\"Bill To\") }}:</b> {{ doc.customer }}\n				{% if doc.address_display != None and doc.address_display != \"\" %}\n				<br/><b>{{ _(\"Address\") }}:</b> {{ doc.address_display }}\n				{% endif %}	\n			</p>\n		</div>\n<div style=\"float: left; width: 50%\">\n<hr/>\n			<p style=\"font-size: 12px;\">\n				<b>{{ _(\"For/Ship To\") }}:</b> {{ doc.customer }}\n				{% if doc.shipping_address != None and doc.shipping_address != \"\" %}\n				<br/><b>{{ _(\"Address\") }}:</b> {{ doc.shipping_address }}	\n				{% endif %}\n			</p>\n		</div>\n<br>\n<br>\n	<table class=\"table table-condensed cart no-border\" style=\"border: 1px solid !important;\" >\n		<thead>\n			<tr style=\"font-size: 13px;\">\n				<th width=\"5%\">{{ _(\"S.No.\") }}</th>\n				<th width=\"15%\">{{ _(\"Item Code\") }}</th>\n				<th width=\"25%\">{{ _(\"Description\") }}</th>\n				<th width=\"10%\">{{ _(\"Quantity\") }}</th> \n				<th width=\"20%\" style=\"text-align:right;\">{{ _(\"Unit Price\") }}</b></th>\n				<th width=\"25%\" class=\"text-right\">{{ _(\"Total Amount\") }}</th>\n			</tr>\n		</thead>\n		<tbody>\n			\n			{%- for item in doc.items -%}\n			\n			<tr style=\"font-size:12px;\">\n				<td style=\"border-right: 1px solid #d1d8dd;\">{{item.idx}}</td>\n				<td style=\"border-right: 1px solid #d1d8dd;\">{{ item.item_code}}</td>\n				<td style=\"border-right: 1px solid #d1d8dd;\">{{ item.description}}</td>\n				<td style=\"border-right: 1px solid #d1d8dd;\">&nbsp;{{ item.qty }}&nbsp;{{ item.uom }}</td>\n				<td style=\"text-align:right;border-right: 1px solid #d1d8dd;\">{{ item.get_formatted(\"price_list_rate\") }}</td>\n			<td style=\"text-align:right;\">{{ item.get_formatted(\"amount\")  }} AED</td>\n			</tr>\n			\n			{%- endfor -%}\n		</tbody>\n	</table>\n<table class=\"table table-condensed no-border\">\n	<tbody>\n		<tr>\n			<td class=\"text-right\" style=\"width: 70%;border-top: none;\">\n				<b>{{ _(\"Total Before VAT\") }}</b>\n			</td>\n			<td class=\"text-right\" style=\"border-top: none;\">\n				{{ doc.get_formatted(\"net_total\") }} AED\n			</td>\n		</tr>\n		{%- for row in doc.taxes -%}\n		{%- if row.included_in_print_rate -%}\n		<tr>\n			<td class=\"text-right\" style=\"width: 70%;border-top: none;\">\n				<b>{{ _(\"VAT Incl.\") }}</b>\n			</td>\n			<td class=\"text-right\" style=\"border-top: none;\">\n				{{ row.get_formatted(\"tax_amount_after_discount_amount\", doc) }} AED\n			</td>\n		<tr>\n		{%- endif -%}\n		{%- endfor -%}\n		{%- for row in doc.taxes -%}\n		{%- if not row.included_in_print_rate -%}\n		<tr>\n			<td class=\"text-right\" style=\"width: 70%;border-top: none;\">\n				{{ row.description }}\n			</td>\n			<td class=\"text-right\" style=\"border-top: none;\">\n				{{ row.get_formatted(\"tax_amount\", doc) }} AED\n			</td>\n		</tr>\n		{%- endif -%}\n		{%- endfor -%}\n		{%- if doc.discount_amount -%}\n		<tr>\n			<td class=\"text-right\" style=\"width: 75%;border-top: none;\">\n				<b>{{ _(\"Discount\") }}</b>\n			</td>\n			<td class=\"text-right\" style=\"border-top: none;\">\n				{{ doc.get_formatted(\"discount_amount\") }} AED\n			</td>\n		</tr>\n		{%- endif -%}\n				<tr>\n			<td class=\"text-right\" style=\"width: 75%;border-top: none;\">\n				<b>{{ _(\"Grand Total\") }}</b>\n			</td>\n			<td class=\"text-right\" style=\"border-top: none;\">\n				{{ doc.get_formatted(\"grand_total\") }} AED\n			</td>\n		</tr>\n		<tr>\n			<td class=\"text-right\" style=\"width: 70%;border-top: none;\">\n				<b>{{ _(\"Total Paid Amount\") }}</b>\n			</td>\n			<td class=\"text-right\" style=\"border-top: none;\">\n				{{ doc.get_formatted(\"paid_amount\") }} AED\n			</td>\n		</tr>\n		<tr>\n			<td class=\"text-right\" style=\"width: 75%;border-top: none;\">\n				<b>{{ _(\"Total Outstanding Amount\") }}</b>\n			</td>\n			<td class=\"text-right\" style=\"border-top: none;\">\n				{{ doc.get_formatted(\"outstanding_amount\") }} AED\n			</td>\n		</tr>		\n		<tr>\n			<td class=\"text-right\" style=\"width: 70%;border-top: none;\">\n				<b>{{ _(\"Change Amount\") }}</b>\n			</td>\n			<td class=\"text-right\" style=\"border-top: none;\">\n				{{ doc.get_formatted(\"change_amount\") }} AED\n			</td>\n		</tr>\n		<tr>\n			<td class=\"text-right\" style=\"width: 70%;border-top: none;\">\n				<b>{{ _(\"In Words Amount\") }}</b>\n			</td>\n			<td class=\"text-right\" style=\"border-top: none;\">\n				{{ doc.get_formatted(\"in_words\") }} AED\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p><b>Tax Breakup:</b></p>\n<div style=\"font-size: 12px\">\n	{{ doc.other_charges_calculation }}\n</div>\n<p>{{ doc.terms or \"\" }}</p>\n	<script>\n		var currentdate = new Date(); \n		var datetime = currentdate.getDate() + \"/\"\n		+ (currentdate.getMonth()+1)  + \"/\" \n		+ currentdate.getFullYear() + \" \"   \n		+ currentdate.getHours() + \":\"  \n		+ currentdate.getMinutes() + \":\" \n		+ currentdate.getSeconds();\n\n		$(\'.curr_date_time\').text(datetime);\n\n	</script>', NULL, NULL, NULL, 1, 'No', 0, NULL, 'Server', NULL, 'Sales Invoice', 0, 0);
INSERT INTO `tabPrint Format` VALUES ('Drop Shipping Format', '2015-12-04 13:11:16.992997', '2016-03-07 20:49:39.443328', 'Administrator', 'Administrator', 0, NULL, NULL, NULL, 0, NULL, 0, 1, 'Default', NULL, '<div class=\"page-break\">\n    <div class=\"print-heading\">\n		<h2>Purchase Order<br>\n	        <small>{{doc.name}}</small>\n        </h2>\n    </div>\n    <div class=\"row section-break\">\n        <div class=\"col-xs-6 column-break\">\n                <div class=\"row\">\n					<div class=\"col-xs-5 text-right\">\n						<label>Supplier Name</label>\n					</div>\n					<div class=\"col-xs-7  value\">\n						{{doc.supplier_name}}\n					</div>\n				</div>\n				{% if doc.address_display %}\n	                <div class=\"row\">\n						<div class=\"col-xs-5 text-right\">\n							<label>Address</label>\n						</div>\n						<div class=\"col-xs-7  value\">\n							{{doc.address_display}}\n						</div>\n					</div>\n				{%- endif -%}\n				{% if doc.contact_display %}\n	                <div class=\"row\">\n						<div class=\"col-xs-5 text-right\">\n							<label>Contact</label>\n						</div>\n						<div class=\"col-xs-7  value\">\n							{{doc.contact_display}}\n						</div>\n					</div>\n				{%- endif -%}\n				{% if doc.contact_mobile %}\n	                <div class=\"row\">\n						<div class=\"col-xs-5 text-right\">\n							<label>Mobile No</label>\n						</div>\n						<div class=\"col-xs-7  value\">\n							{{doc.contact_mobile}}\n						</div>\n					</div>\n				{%- endif -%}\n        </div>\n        \n        <div class=\"col-xs-6 column-break\">\n            <div class=\"row\">\n				<div class=\"col-xs-5 text-right\">\n					<label>Date</label>\n				</div>\n				<div class=\"col-xs-7  value\">\n					{{doc.transaction_date}}\n				</div>\n			</div>\n            <div class=\"row\">\n				<div class=\"col-xs-5 text-right\">\n					<label>Customer Name</label>\n				</div>\n				<div class=\"col-xs-7  value\">\n					{{doc.customer_name}}\n				</div>\n			</div>\n			{% if doc.shipping_address_display %}\n	            <div class=\"row\">\n					<div class=\"col-xs-5 text-right\">\n						<label>Customer Address</label>\n					</div>\n					<div class=\"col-xs-7  value\">\n						{{doc.shipping_address_display}}\n					</div>\n				</div>\n			{%- endif -%}\n			{% if doc.customer_contact_display %}\n	            <div class=\"row\">\n					<div class=\"col-xs-5 text-right\">\n						<label>Customer Contact</label>\n					</div>\n					<div class=\"col-xs-7  value\">\n						{{doc.customer_contact_display}}\n					</div>\n				</div>\n			{%- endif -%}\n			{% if doc.customer_contact_mobile %}\n	            <div class=\"row\">\n					<div class=\"col-xs-5 text-right\">\n						<label>Customer Mobile No</label>\n					</div>\n					<div class=\"col-xs-7  value\">\n						{{doc.customer_contact_mobile}}\n					</div>\n				</div>\n			{%- endif -%}\n        </div>\n    </div>\n	<table class=\"table table-bordered\">\n		<tbody>\n			<tr>\n				<th>Sr</th>\n				<th>Item Name</th>\n				<th>Description</th>\n				<th class=\"text-right\">Qty</th>\n				<th class=\"text-right\">Rate</th>\n				<th class=\"text-right\">Amount</th>\n			</tr>\n			{%- for row in doc.items -%}\n			<tr>\n				<td style=\"width: 3%;\">{{ row.idx }}</td>\n				<td style=\"width: 20%;\">\n					{{ row.item_name }}\n					{% if row.item_code != row.item_name -%}\n					<br>Item Code: {{ row.item_code}}\n					{%- endif %}\n				</td>\n				<td style=\"width: 37%;\">\n					<div style=\"border: 0px;\">{{ row.description }}</div></td>\n				<td style=\"width: 10%; text-align: right;\">{{ row.qty }} {{ row.uom or row.stock_uom }}</td>\n				<td style=\"width: 15%; text-align: right;\">{{\n					row.get_formatted(\"rate\", doc) }}</td>\n				<td style=\"width: 15%; text-align: right;\">{{\n					row.get_formatted(\"amount\", doc) }}</td>\n			</tr>\n			{%- endfor -%}\n		</tbody>\n	</table>\n    <div class=\"row section-break\">\n        <div class=\"col-xs-6 column-break\">\n        </div>\n        <div class=\"col-xs-6 column-break\">\n                <div class=\"row\">\n					<div class=\"col-xs-5 text-right\">\n						<label>Total</label>\n					</div>\n					<div class=\"col-xs-7 text-right value\">\n						{{doc.total}}\n					</div>\n				</div>\n                <div class=\"row\">\n					<div class=\"col-xs-5 text-right\">\n						<label>Grand Total</label>\n					</div>\n					<div class=\"col-xs-7 text-right value\">\n						{{doc.grand_total}}\n					</div>\n				</div>\n                <div class=\"row\">\n					<div class=\"col-xs-5 text-right\">\n						<label>In Words</label>\n					</div>\n					<div class=\"col-xs-7 text-right value\">\n						{{doc.in_words}}\n					</div>\n				</div>\n        </div>\n    </div>\n</div>', NULL, NULL, NULL, 0, 'Yes', 0, NULL, 'Server', NULL, 'Purchase Order', 0, 0);
INSERT INTO `tabPrint Format` VALUES ('Full Payment Entry 2', '2018-07-11 09:07:01.588867', '2018-08-16 08:57:09.827515', 'Administrator', 'Administrator', 0, NULL, NULL, NULL, 0, 'Accounts', 0, 1, 'Default', NULL, '<style> \n.print-format table, .print-format tr, \n.print-format td, .print-format div, .print-format p {\n	font-family: Arial;\n	line-height: 170%;\n	vertical-align: middle;\n}\n@media screen {\n	.print-format {\n	\n	padding: 0.05in !important;\n	min-height: auto;\n	}\n	.print-format th {\n	background-color: black !important;\n	border-bottom: 0px !important;\n	color: white !important;\n	}\n	\n	.table {\n	margin: 10px 0px !important;\n	}	\n}\n.print-preview {\n	padding: 0px;\n	margin: auto;\n	min-height: auto !important;\n}\n</style>\n{% set companydoc = frappe.get_doc(\"Company\", doc.company) %}\n{% set Customerdoc = frappe.get_doc(\"Customer\", doc.customer) %}\n{% set Paymententry = frappe.get_doc(\"Payment Entry\", doc.payment)%}\n\n<div style=\"border: 2px solid;height: auto;float: left; width:100% \">\n	{% if companydoc.pos_logo != None %}\n	<div style=\"float: left;width: 20%;\">\n	<p>\n	\n	<img src={{ companydoc.pos_logo }} style=\"max-height: 100px;margin-top: 15%;width: 120px;margin-left: 5px;\"><br>\n	\n	</p>\n	</div>\n	<div style=\"float: left;width: 48%;border-right: 2px solid;height: 188px;\">\n	<p style=\"font-size: 12px;margin-top: 15px;\">\n	<b style=\" font-size: 20px;\">{{ doc.company }}</b><br>{{ doc.company_pos_address }}</p>\n	{% if companydoc.vat_number != None %}\n	<p style=\"font-size: 12px;\">TRN # {{ companydoc.vat_number }}</p>\n	{% endif %}	\n	<br/>\n	</div>\n{% else %}\n	<div style=\"float: left;width: 68%;border-right: 2px solid;height: auto;\">\n	<p style=\"font-size: 12px;margin-top: 15px;margin-left: 10px;\">\n	<b style=\" font-size: 20px;\">{{ doc.company }}</b><br>{{ doc.company_pos_address }}</p>\n	<p style=\"font-size: 12px;margin-left: 10px;\">TRN # {{ companydoc.vat_number }}</p>\n	<br/>\n	</div>\n{% endif %}\n	<div style=\"float: right;width: 32%;\">\n	<div style=\"border-bottom: 2px solid;\">\n	{% if doc.payment_type == \'Receive\' %}\n	<p style=\"font-size: 20px;font-weight: bold;margin-left: 10px;\">RECEIVE ENTRY</p>\n	{% else %}\n	<p style=\"font-size: 20px;font-weight: bold;margin-left: 10px;\">PAYMENT ENTRY</p>\n	{% endif %}\n	</div>\n	<p style=\"font-size: 12px;margin-left: 10px;\">\n	{% if doc.payment_type == \'Receive\' %}\n	<b>{{ _(\"Receive Number\") }}:</b>{{ doc.name }} <br>\n	{% else %}\n	<b>{{ _(\"Payment Number\") }}:</b>{{ doc.name }} <br>\n	{% endif %}\n	<b>{{ _(\"Date\") }}:</b>{{ doc.posting_date }} <br>\n	<b>{{ _(\"Mode Of Payment\") }}:</b>{{ doc.mode_of_payment }} <br>\n	<b>{{ _(\"Served By\") }}:</b>{{ doc.served_by }}<br>\n	{% if doc.reference_no != None and doc.reference_no != \"\" %}\n	<b>{{ _(\"Cheque No\") }}:</b>{{ doc.reference_no }} <br>\n	{% endif %}\n	{% if doc.drawn_on != None and doc.drawn_on != \"\" %}\n	<b>{{ _(\"Bank Name\") }}:</b> {{ doc.drawn_on }}<br>\n	{% endif %}\n	{% if doc.reference_date != None and doc.reference_date != \"\" %}\n	<b>{{ _(\"Cheque Date\") }}:{{ doc.reference_date }}</b> \n	{% endif %}\n	\n	</p>\n	</div>\n	</div>\n	<div style=\"border-bottom: 2px solid;border-left: 2px solid;border-right: 2px solid;height: auto;width: 100%;float: left;margin-bottom: 10px;\">\n	<div style=\"float: left; width: 45%\">\n	<p style=\"font-size: 12px;margin-left: 3%;\">\n	<b>{{ _(\"Bill To\") }}:</b> <b>{{ doc.party_name }}</b>\n	{% if doc.shipping_address != None and doc.shipping_address != \"\" %}\n	<br/><b>{{ _(\"Address\") }}:</b> {{ doc.shipping_address }}\n	{% endif %}\n	{% if Customerdoc.mobile_number != None and Customerdoc.mobile_number != \"\" %}\n	<br/><b>{{ _(\"Mobile\") }}:</b> {{ Customerdoc.mobile_number }}\n	{% endif %}\n	\n	\n	</p>\n	</div>\n	</div>\n\n\n	<br>\n\n	<table class=\"table table-condensed cart no-border\" style=\"border: 1px solid !important;\" >\n	<thead>\n	<tr style=\"font-size: 13px;\">\n	<th width=\"5%\">{{ _(\"S.No.\") }}</th>\n	<th width=\"15%\">{{ _(\"Type\") }}</th>\n	<th width=\"25%\">{{ _(\"Name\") }}</th>\n	<th width=\"15%\">{{ _(\"Due Date\") }}</th> \n	<th width=\"20%\" style=\"text-align:right;\">{{ _(\"Outstanding\") }}</b></th>\n	<th width=\"20%\" class=\"text-right\">{{ _(\"Recived\") }}</th> \n	</tr>\n	</thead>\n	<tbody>\n	\n	{%- for payment in doc.references -%}\n	\n	<tr style=\"font-size:12px;\">\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{payment.idx}}</td>\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{payment.reference_doctype}}</td>\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{payment.reference_name}}</td>\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{payment.due_date}}</td>\n	<td style=\"text-align:right;border-right: 1px solid #d1d8dd;\">{{payment.outstanding_amount}}</td>\n	<td style=\"text-align:right;\">{{payment.allocated_amount}}</td>\n	</tr>\n	\n	{%- endfor -%}\n	</tbody>\n	</table>\n\n	<br>\n{% if doc.mode_of_payment == \'Cheque\' %}\n	<table style=\"width:100%\">\n\n  <tr>\n    <td>Cheque/Reference No</td>\n    <td><b>{{ doc.reference_no }}</b></td>\n    <td>Cheque/Reference Date</td>\n    <td><b>{{ doc.reference_date }}</b></td>\n  </tr>\n</table>\n{%- endif -%}\n<br>\n<table style=\"width:100%\">\n\n  <tr>\n    <td>Remarks</td>\n    <td><b>{{ frappe.utils.money_in_words(doc.total_allocated_amount) }}</b></td>\n  </tr>\n</table>\n<br>\n<br>\n	<table style=\"width:100%\">\n  <tr>\n    <td style=\"text-align: center;\">______________</td>\n    <td style=\"text-align: center;\">______________</td>\n  </tr>\n  <tr>\n    <td style=\"text-align: center;\">Prepared By</td>\n    <td style=\"text-align: center;\">Recived By</td>\n  </tr>\n</table>\n<hr>\n	<p>{{ doc.terms or \"\" }}</p>\n<p class=\"text-center\" style=\"font-size:11px\"><b>{{ _(\"Thank you, please visit again.\") }}</b></p>\n\n<p class=\"text-center\" style=\"font-size:11px\"><b>{{ _(\"Powered by: www.uaepos.com\") }}</b></p>\n<script>\n	<script>\n	var currentdate = new Date(); \n	var datetime = currentdate.getDate() + \"/\"\n	+ (currentdate.getMonth()+1) + \"/\" \n	+ currentdate.getFullYear() + \" \" \n	+ currentdate.getHours() + \":\" \n	+ currentdate.getMinutes() + \":\" \n	+ currentdate.getSeconds();\n\n	$(\'.curr_date_time\').text(datetime);\n\n	</script>', NULL, NULL, NULL, 1, 'No', 0, NULL, 'Server', NULL, 'Payment Entry', 0, 0);
INSERT INTO `tabPrint Format` VALUES ('Full Purchase Invoice', '2018-03-28 14:21:28.201046', '2018-03-28 14:22:07.814105', 'Administrator', 'Administrator', 0, NULL, NULL, NULL, 0, 'Accounts', 0, 1, 'Default', NULL, '<style>\n	.print-format table, .print-format tr, \n	.print-format td, .print-format div, .print-format p {\n	font-family: Arial;\n	line-height: 170%;\n	vertical-align: middle;\n	}\n	@media screen {\n	.print-format {\n	\n	padding: 0.05in !important;\n	min-height: auto;\n	}\n	.print-format th {\n background-color: black !important;\n border-bottom: 0px !important;\n color: white !important;\n	}\n	\n	.table {\n margin: 10px 0px !important;\n	}	\n	}\n.print-preview {\n padding: 0px;\n margin: auto;\n min-height: auto !important;\n}\n</style>\n\n{% set companydoc = frappe.get_doc(\"Company\", doc.company) %}\n{% set Supplierdoc = frappe.get_doc(\"Supplier\", doc.supplier) %}\n\n<div>\n	<div style=\"float: left;width: 20%;\">\n	<p>\n	{% if companydoc.pos_logo != None %}\n	<img src={{ companydoc.pos_logo }} style=\"max-height: 100px\"><br>\n	{% endif %}\n	</p>\n	</div>\n	<div style=\"float: left;width: 50%;\">\n	<p>\n	<p style=\"font-size: 12px;margin-top: 15px;margin-left: 10px;margin-right: 10px;\">\n	<b style=\" font-size: 20px;\">{{ doc.company }}</b><br>\n	{{ doc.company_pos_address }}</p>\n	<p style=\"font-size: 12px;margin-left: 10px;\">TRN # {{ companydoc.vat_number }}</p>\n	<br/>\n	</p>\n	</div>\n	<div style=\"float: right;width: 30%;\">\n	<div style=\"font-size: 20px;\"><b>PURCHASE INVOICE</b></div>\n	<p style=\"font-size: 12px;\">\n	<b>{{ _(\"Voucher No\") }}:</b> {{ doc.name }}<br>\n	<b>{{ _(\"Invoice Date\") }}:</b> {{ doc.get_formatted(\"posting_date\") }} {{ doc.get_formatted(\"posting_time\") }}<br>\n	<b>{{ _(\"Created By\") }}:</b> {{doc.modified_by }}<br>\n	<b>{{ _(\"Payment Status\") }}:</b> {{ doc.status }}<br>\n <b>{{ _(\"Print Date\") }}:</b> {{ frappe.utils.formatdate(frappe.utils.today(), \'dd-MM-YYYY\') }} {{ frappe.utils.now_datetime().strftime(\'%I:%M %p\') }}\n	</p>\n	</div>\n	</div>\n	\n<div>\n	<div style=\"float: left; width: 50%\">\n	<p style=\"font-size: 12px;margin-left: 10%;\">\n	<b>{{ _(\"Supplier Name\") }}:</b> {{ Supplierdoc.supplier_name }}<br/>\n	{% if Supplierdoc.address_display != None %}\n	<b>{{ _(\"Address\") }}:</b> {{ doc.address_display }}\n	{% endif %}\n{% if Supplierdoc.mobile_number != None %}\n	<b>{{ _(\"Mobile\") }}:</b> {{ Supplierdoc.mobile_number }}<br/>\n	{% endif %}\n	{% if Supplierdoc.tax_id != None %}\n	<b>{{ _(\"TRN\") }}:</b> {{ Supplierdoc.tax_id }}\n	{% endif %}\n	\n	\n	</p>\n	</div>\n	<div style=\"float: right; width: 50%\"></div>\n	</div>\n\n<br>\n\n<table class=\"table table-condensed cart no-border\" style=\"width: 90%;margin-left: 40px !important;border: 1px solid !important;\" >\n	<thead>\n	<tr style=\"font-size: 13px;\">\n	<th width=\"5%\">{{ _(\"Sr\") }}</th>\n	<th width=\"20%\">{{ _(\"Item Code\") }}</th>\n	<th width=\"30%\">{{ _(\"Item Name\") }}</th>\n	<th width=\"10%\">{{ _(\"Quantity\") }}</th> \n	<th width=\"15%\" style=\"text-align:center;\">{{ _(\"Current Price (AED)\") }}</b></th>\n	<th width=\"20%\" class=\"text-right\">{{ _(\"Amount (AED)\") }}</th>\n	\n	</tr>\n	</thead>\n	<tbody>\n	\n	{%- for item in doc.items -%}\n	<tr style=\"font-size:12px;\">\n	<td>{{item.idx}}</td>\n	<td>{{ item.item_code}}</td>\n	<td>{{ item.item_name }}</td>\n	<td>&nbsp;{{ item.qty }}&nbsp;{{ item.uom }}</td>\n	<td style=\"text-align:right;\">{{ item.get_formatted(\"base_rate\") }}</td>\n	{% set amount_total = item.item_with_tax_amount %}\n	{% if amount_total == 0 %}\n	<td class=\"text-right\">{{ item.get_formatted(\"amount\") }} </td>	\n	{% else %}\n	<td class=\"text-right\">{{ amount_total }} </td>	\n	{% endif%}\n	\n	\n	</tr>\n	\n	{%- endfor -%}\n	</tbody>\n</table>\n\n<table class=\"table table-condensed no-border\" style=\"width: 85%;margin-left: 85px !important;\" >\n	<tbody>\n	{%- if doc.total_discount -%}\n	<tr>\n	<td class=\"text-right\" style=\"width: 50%;font-size:13px;font-weight:bold;border-top: none;\">\n	<b>	{{ _(\"Total Discount\") }} </b>\n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;border-top: none;\">\n	<b>{{ doc.get_formatted(\"total_discount\") }} {{ companydoc.default_currency}}</b>\n	</td>\n	</tr>\n	{%- endif -%}	\n	<tr>\n	<td class=\"text-right\" style=\"width:80%;font-size:13px;font-weight:bold;border-top: none;\">\n	{{ _(\"Total Before Tax\") }} \n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;padding-bottom: 10px !important;border-top: none; padding-right:53px;\">\n	<b>{{ doc.get_formatted(\"net_total\") }} {{ companydoc.default_currency}}</b>\n	</td>\n	</tr >\n	{%- for row in doc.taxes -%}\n	{%- if not row.included_in_print_rate -%}\n	<tr style=\"font-size:13px;font-weight:bold;\">\n	<td class=\"text-right\" style=\"border-top: none;\">\n	{{ row.description }}\n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;border-top: none;\">\n	<b>{{ row.get_formatted(\"tax_amount\", doc) }} {{ companydoc.default_currency}}</b>\n	</td>\n	</tr>\n	{%- endif -%}\n	{%- endfor -%}\n	<tr>\n	<td class=\"text-right\" style=\"font-size:15px;font-weight:bold;border-top: none;\">\n	<b>{{ _(\"Grand Total\") }}</b>\n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:15px;border-top: none;\">\n	<b>{{ doc.get_formatted(\"grand_total\") }} {{ companydoc.default_currency}}</b>\n	</td>\n	</tr>\n	\n	<tr>\n	<td class=\"text-right\" style=\"width: 75%;font-size:13px;font-weight:bold;border-top: none;\">\n	<b>{{ _(\"Outstanding Amount\") }}</b>\n	</td>\n	<td class=\"text-right\" style=\"font-size:12px;font-weight:bold;border-top: none;\">\n	{{ doc.get_formatted(\"outstanding_amount\") }} {{ companydoc.default_currency}}\n	</td>\n	</tr>	\n	<tr>\n	\n	<td colspan=\"2\" class=\"text-left\" style=\"width:100%;font-size:12px;border-top: none;\">\n	<b>{{ doc.in_words }}</b>\n	</td>\n	\n	</tr>\n</tbody>\n</table>\n\n\n<p>{{ doc.terms or \"\" }}</p>', NULL,