INSERT INTO `tabPrint Format` VALUES ('New Quotation', '2018-09-19 11:42:42.080378', '2018-09-19 12:21:22.036752', 'Administrator', 'Administrator', 0, NULL, NULL, NULL, 0, 'Selling', 0, 1, 'Default', NULL, '<style> \n.print-format table, .print-format tr, \n.print-format td, .print-format div, .print-format p {\n	font-family: Arial;\n	line-height: 170%;\n	vertical-align: middle;\n}\n@media screen {\n	.print-format {\n	\n	padding: 0.05in !important;\n	min-height: auto;\n	}\n	.print-format th {\n	background-color: black !important;\n	border-bottom: 0px !important;\n	color: white !important;\n	}\n	\n	.table {\n	margin: 10px 0px !important;\n	}	\n}\n.print-preview {\n	padding: 0px;\n	margin: auto;\n	min-height: auto !important;\n}\n</style>\n{% set companydoc = frappe.get_doc(\"Company\", doc.company) %}\n{% set Customerdoc = frappe.get_doc(\"Customer\", doc.customer) %}\n\n<div style=\"border: 2px solid;height: auto;float: left; width:100% \">\n	{% if companydoc.pos_logo != None %}\n	<div style=\"float: left;width: 20%;\">\n	<p>\n	\n	<img src={{ companydoc.pos_logo }} style=\"max-height: 100px;margin-top: 15%;width: 120px;margin-left: 5px;\"><br>\n	\n	</p>\n	</div>\n	<div style=\"float: left;width: 48%;border-right: 2px solid;height: 188px;\">\n	<p style=\"font-size: 12px;margin-top: 15px;\">\n	<b style=\" font-size: 20px;\">{{ doc.company }}</b><br>{{ doc.company_pos_address }}</p>\n	{% if companydoc.vat_number != None %}\n	<p style=\"font-size: 12px;\">TRN # {{ companydoc.vat_number }}</p>\n	{% endif %}	\n	<br/>\n	</div>\n{% else %}\n	<div style=\"float: left;width: 68%;border-right: 2px solid;height: auto;\">\n	<p style=\"font-size: 12px;margin-top: 15px;margin-left: 10px;\">\n	<b style=\" font-size: 20px;\">{{ doc.company }}</b><br>{{ doc.company_pos_address }}</p>\n	<p style=\"font-size: 12px;margin-left: 10px;\">TRN # {{ companydoc.vat_number }}</p>\n	<br/>\n	</div>\n{% endif %}\n	<div style=\"float: right;width: 32%;\">\n	<div style=\"border-bottom: 2px solid;\">\n	<p style=\"font-size: 20px;font-weight: bold;margin-left: 10px;\">QUOTATION</p>\n	</div>\n	<p style=\"font-size: 12px;margin-left: 10px;\">\n	<b>{{ _(\"Quotation No\") }}:</b>{{ doc.name }}<br>\n	<b>{{ _(\"Invoice Date\") }}:</b> {{ doc.transaction_date }}<br>\n	<b>{{ _(\"Vaild Till\") }}:</b> {{ doc.valid_till }}<br>\n	<b>{{ _(\"Served By\") }}:</b> {{ doc.owner }}<br>\n	<b>{{ _(\"Print Date\") }}:</b> {{ frappe.utils.formatdate(frappe.utils.today(), \'dd-MM-YYYY\') }} {{ frappe.utils.now_datetime().strftime(\'%I:%M %p\') }}\n	\n	</p>\n	</div>\n	</div>\n	<div style=\"border-bottom: 2px solid;border-left: 2px solid;border-right: 2px solid;height: auto;width: 100%;float: left;margin-bottom: 10px;\">\n	<div style=\"float: left; width: 45%\">\n	<p style=\"font-size: 12px;margin-left: 3%;\">\n	<b>{{ _(\"Bill To\") }}:</b> <b>{{ doc.customer_name }}</b>\n	{% if doc.contact_display != None and doc.contact_display != \"\" %}\n	<br/><b>{{ _(\"Contact Person\") }}:</b> {{ doc.contact_display }}\n	{% endif %}\n	{% if doc.address_display != None and doc.address_display != \"\" %}\n	<br/><b>{{ _(\"Address\") }}:</b> {{ doc.address_display }}\n	{% endif %}\n	{% if Customerdoc.mobile_number != None and Customerdoc.mobile_number != \"\" %}\n	<br/><b>{{ _(\"Mobile\") }}:</b> {{ Customerdoc.mobile_number }}\n	{% endif %}\n	{% if Customerdoc.tax_id != None %}\n	<br/><b>{{ _(\"TRN\") }}:</b> {{ Customerdoc.tax_id }}\n	{% endif %}\n	\n	\n	</p>\n	</div>\n	<div style=\"float: right; width: 45%\">\n	</div>\n	</div>\n\n\n	<br>\n\n	<table class=\"table table-condensed cart no-border\" style=\"border: 1px solid !important;\" >\n	<thead>\n	<tr style=\"font-size: 13px;\">\n	<th width=\"5%\">{{ _(\"S.No.\") }}</th>\n	<th width=\"15%\">{{ _(\"Item Code\") }}</th>\n	<th width=\"35%\">{{ _(\"Description\") }}</th>\n	<th width=\"10%\">{{ _(\"Quantity\") }}</th> \n	<th width=\"10%\" style=\"text-align:right;\">{{ _(\"Rate\") }}</b></th>\n	<th width=\"25%\" class=\"text-right\">{{ _(\"Total Amount\") }}</th>\n	</tr>\n	</thead>\n	<tbody>\n	\n	{%- for item in doc.items -%}\n	\n	<tr style=\"font-size:12px;\">\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{item.idx}}</td>\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{ item.item_code}}</td>\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{ item.description}}</td>\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{ item.qty }}{{ item.uom }}</td>\n	<td style=\"text-align:right;border-right: 1px solid #d1d8dd;\">{{ item.base_rate }}</td>\n\n	<td style=\"text-align:right;\">{{ item.get_formatted(\"amount\") }} {{ companydoc.default_currency}}</td>\n	</tr>\n	\n	{%- endfor -%}\n	</tbody>\n	</table>\n\n	<table class=\"table table-condensed no-border\" style=\"width: 100%;\" >\n	<tbody>\n\n	{%- if doc.total_discount -%}\n	<tr>\n	<td style=\"border: none;\"></td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b>	{{ _(\"Total Discount\") }} </b>\n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;border-top: none;\">\n	<b>{{ doc.get_formatted(\"total_discount\") }} {{ companydoc.default_currency}}</b>\n	</td>\n	</tr>\n	{%- endif -%}	\n	<tr>\n	<td rowspan=\"3\" style=\"border: 1px solid;\"></td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	{{ _(\"Total Before Tax\") }} \n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;padding-bottom: 10px !important;border-top: none; padding-right:53px;\">\n	<b>{{ doc.get_formatted(\"net_total\") }} {{ companydoc.default_currency}}</b>\n	</td>\n	</tr >\n	\n	{%- for row in doc.taxes -%}\n	\n			<tr style=\"font-size:13px;font-weight:bold;\">\n				\n				<td class=\"text-right\" style=\"border-top: none;\">\n					{{ _(\"Tax Amount Payable\") }}\n					\n				</td>\n				<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;border-top: none;\">\n					<b>{{ row.get_formatted(\"tax_amount\", doc) }}  {{  companydoc.default_currency}}</b>\n				</td>\n			</tr>\n		\n			{%- endfor -%}\n	{%- if doc.write_off_amount -%}\n	<tr>\n	<td style=\"border: none;\"></td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b>{{ _(\"Write off Amount\") }}</b>\n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;border-top: none;\">\n	<b>{{ doc.get_formatted(\"write_off_amount\") }} {{ companydoc.default_currency}}</b>\n	</td>\n	\n	</tr>\n	{%- endif -%}\n	<tr>\n	\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b><u>{{ _(\"Gross Amount Payable\") }}</u></b>\n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;border-top: none;\">\n	\n	<b><u>{{ doc.get_formatted(\"grand_total\") }} {{ companydoc.default_currency}}</u></b>\n	</td>\n	\n	</tr>\n	\n	\n	\n	<tr>\n	<td></td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b></b>\n	</td>\n	<td class=\"text-right\" style=\"font-size:12px;font-weight:bold;border-top: none;\">\n	\n	</td>\n	</tr>\n	<tr>\n	<td style=\"border: none;\"></td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b></b>\n	</td>\n	<td class=\"text-right\" style=\"font-size:12px;font-weight:bold;border-top: none;\">\n	</td>\n	</tr>	\n	<tr>\n	<td class=\"text-left\" style=\"width: 50%;font-size:12px;border-top: none;\">\n	<b>{{ doc.in_words }}</b>\n	</td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b></b>\n	</td>\n	<td class=\"text-right\" style=\"font-size:12px;font-weight:bold;border-top: none;\">\n	</td>\n	</tr>\n	\n	</tbody>\n	</table>\n<br>\n<br>\n<br>\n<br>\n	<table style=\"width:100%\">\n  <tr>\n    <td style=\"text-align: center;\">______________</td>\n 	<td style=\"text-align: center;\">______________</td>\n    <td style=\"text-align: center;\">______________</td>\n  </tr>\n  <tr>\n    <td style=\"text-align: center;\">Prepared By</td>\n    <td style=\"text-align: center;\">Approved By</td>\n    <td style=\"text-align: center;\">Recived By</td>\n  </tr>\n</table>\n<hr>\n	<p>{{ doc.terms or \"\" }}</p>\n<p class=\"text-center\" style=\"font-size:11px\"><b>{{ _(\"This is system generated invoice, doesn\'t require stamp and signature.\") }}</b></p>\n<p class=\"text-center\" style=\"font-size:11px\"><b>{{ _(\"Thank you, please visit again.\") }}</b></p>\n\n<p class=\"text-center\" style=\"font-size:11px\"><b>{{ _(\"Powered by: www.uaepos.com\") }}</b></p>\n<script>\n	<script>\n	var currentdate = new Date(); \n	var datetime = currentdate.getDate() + \"/\"\n	+ (currentdate.getMonth()+1) + \"/\" \n	+ currentdate.getFullYear() + \" \" \n	+ currentdate.getHours() + \":\" \n	+ currentdate.getMinutes() + \":\" \n	+ currentdate.getSeconds();\n\n	$(\'.curr_date_time\').text(datetime);\n\n	</script>', NULL, NULL, NULL, 1, 'No', 0, NULL, 'Server', NULL, 'Quotation', 0, 0);
INSERT INTO `tabPrint Format` VALUES ('New Sales Order Invoice', '2018-09-19 11:38:13.586621', '2018-09-26 18:19:54.856491', 'Administrator', 'Administrator', 0, NULL, NULL, NULL, 0, 'Selling', 0, 1, 'Default', NULL, '<style> \n.print-format table, .print-format tr, \n.print-format td, .print-format div, .print-format p {\n	font-family: Arial;\n	line-height: 170%;\n	vertical-align: middle;\n}\n@media screen {\n	.print-format {\n	\n	padding: 0.05in !important;\n	min-height: auto;\n	}\n	.print-format th {\n	background-color: black !important;\n	border-bottom: 0px !important;\n	color: white !important;\n	}\n	\n	.table {\n	margin: 10px 0px !important;\n	}	\n}\n.print-preview {\n	padding: 0px;\n	margin: auto;\n	min-height: auto !important;\n}\n</style>\n{% set companydoc = frappe.get_doc(\"Company\", doc.company) %}\n{% set Customerdoc = frappe.get_doc(\"Customer\", doc.customer) %}\n\n<div style=\"border: 2px solid;height: auto;float: left; width:100% \">\n	{% if companydoc.pos_logo != None %}\n	<div style=\"float: left;width: 20%;\">\n	<p>\n	\n	<img src={{ companydoc.pos_logo }} style=\"max-height: 100px;margin-top: 15%;width: 120px;margin-left: 5px;\"><br>\n	\n	</p>\n	</div>\n	<div style=\"float: left;width: 48%;border-right: 2px solid;height: 188px;\">\n	<p style=\"font-size: 12px;margin-top: 15px;\">\n	<b style=\" font-size: 20px;\">{{ doc.company }}</b><br>{{ doc.company_pos_address }}</p>\n	{% if companydoc.vat_number != None %}\n	<p style=\"font-size: 12px;\">TRN # {{ companydoc.vat_number }}</p>\n	{% endif %}	\n	<br/>\n	</div>\n{% else %}\n	<div style=\"float: left;width: 68%;border-right: 2px solid;height: auto;\">\n	<p style=\"font-size: 12px;margin-top: 15px;margin-left: 10px;\">\n	<b style=\" font-size: 20px;\">{{ doc.company }}</b><br>{{ doc.company_pos_address }}</p>\n	<p style=\"font-size: 12px;margin-left: 10px;\">TRN # {{ companydoc.vat_number }}</p>\n	<br/>\n	</div>\n{% endif %}\n	<div style=\"float: right;width: 32%;\">\n	<div style=\"border-bottom: 2px solid;\">\n	<p style=\"font-size: 20px;font-weight: bold;margin-left: 10px;\">SALES ORDER</p>\n	</div>\n	<p style=\"font-size: 12px;margin-left: 10px;\">\n	<b>{{ _(\"Sales Order No\") }}:</b> {{ doc.name }}<br>\n	{% if doc.po_no != None and doc.po_no != \'\' %}\n		<b>{{ _(\"P.O No\") }}:</b> {{ doc.po_no }}<br>\n	{% endif %}	\n	<b>{{ _(\"Invoice Date\") }}:</b> {{ doc.transaction_date }}<br>\n	<b>{{ _(\"Delivery Date\") }}:</b> {{ doc.delivery_date }} <br>\n	<b>{{ _(\"Served By\") }}:</b> {{ doc.owner }}<br>\n	{% if doc.sales_team[0] != undefined %}\n	<b>{{ _(\"Sales Person\") }}:</b> {{ doc.sales_team[0][\'sales_person\'] }}<br>\n	{% endif %}\n	<b>{{ _(\"Print Date\") }}:</b> {{ frappe.utils.formatdate(frappe.utils.today(), \'dd-MM-YYYY\') }} {{ frappe.utils.now_datetime().strftime(\'%I:%M %p\') }}\n	\n	</p>\n	</div>\n	</div>\n	<div style=\"border-bottom: 2px solid;border-left: 2px solid;border-right: 2px solid;height: auto;width: 100%;float: left;margin-bottom: 10px;\">\n	<div style=\"float: left; width: 45%\">\n	<p style=\"font-size: 12px;margin-left: 3%;\">\n	<b>{{ _(\"Bill To\") }}:</b> <b>{{ doc.customer_name }}</b>\n	{% if doc.contact_display != None and doc.contact_display != \"\" %}\n	<br/><b>{{ _(\"Contact Person\") }}:</b> {{ doc.contact_display }}\n	{% endif %}\n	{% if doc.address_display != None and doc.address_display != \"\" %}\n	<br/><b>{{ _(\"Address\") }}:</b> {{ doc.address_display }}\n	{% endif %}\n	{% if Customerdoc.mobile_number != None and Customerdoc.mobile_number != \"\" %}\n	<br/><b>{{ _(\"Mobile\") }}:</b> {{ Customerdoc.mobile_number }}\n	{% endif %}\n	{% if Customerdoc.tax_id != None %}\n	<br/><b>{{ _(\"TRN\") }}:</b> {{ Customerdoc.tax_id }}\n	{% endif %}\n	\n	\n	</p>\n	</div>\n	<div style=\"float: right; width: 45%\">\n	</div>\n	</div>\n\n\n	<br>\n\n	<table class=\"table table-condensed cart no-border\" style=\"border: 1px solid !important;\" >\n	<thead>\n	<tr style=\"font-size: 13px;\">\n	<th width=\"5%\">{{ _(\"S.No.\") }}</th>\n	<th width=\"15%\">{{ _(\"Item Code\") }}</th>\n	<th width=\"25%\">{{ _(\"Description\") }}</th>\n	<th width=\"10%\">{{ _(\"Delivery Date\") }}</th>\n	<th width=\"10%\">{{ _(\"Quantity\") }}</th> \n	<th width=\"10%\" style=\"text-align:right;\">{{ _(\"Rate\") }}</b></th>\n	<th width=\"25%\" class=\"text-right\">{{ _(\"Total Amount\") }}</th>\n	</tr>\n	</thead>\n	<tbody>\n	\n	{%- for item in doc.items -%}\n	\n	<tr style=\"font-size:12px;\">\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{item.idx}}</td>\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{ item.item_code}}</td>\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{ item.description}}</td>\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{ item. delivery_date}}</td>\n	<td style=\"border-right: 1px solid #d1d8dd;\">{{ item.qty }}{{ item.uom }}</td>\n	<td style=\"text-align:right;border-right: 1px solid #d1d8dd;\">{{ item.base_net_rate }}</td>\n\n	<td style=\"text-align:right;\">{{ item.get_formatted(\"amount\") }} {{ companydoc.default_currency}}</td>\n	</tr>\n	\n	{%- endfor -%}\n	</tbody>\n	</table>\n\n	<table class=\"table table-condensed no-border\" style=\"width: 100%;\" >\n	<tbody>\n\n	{%- if doc.total_discount -%}\n	<tr>\n	<td style=\"border: none;\"></td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b>	{{ _(\"Total Discount\") }} </b>\n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;border-top: none;\">\n	<b>{{ doc.get_formatted(\"total_discount\") }} {{ companydoc.default_currency}}</b>\n	</td>\n	</tr>\n	{%- endif -%}	\n	<tr>\n	<td rowspan=\"3\" style=\"border: 1px solid;\"></td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	{{ _(\"Total Before Tax\") }} \n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;padding-bottom: 10px !important;border-top: none; padding-right:53px;\">\n	<b>{{ doc.get_formatted(\"net_total\") }} {{ companydoc.default_currency}}</b>\n	</td>\n	</tr >\n	\n	{%- for row in doc.taxes -%}\n	\n			<tr style=\"font-size:13px;font-weight:bold;\">\n				\n				<td class=\"text-right\" style=\"border-top: none;\">\n					{{ _(\"Tax Amount Payable\") }}\n					\n				</td>\n				<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;border-top: none;\">\n					<b>{{ row.get_formatted(\"tax_amount\", doc) }}  {{  companydoc.default_currency}}</b>\n				</td>\n			</tr>\n		\n			{%- endfor -%}\n	{%- if doc.write_off_amount -%}\n	<tr>\n	<td style=\"border: none;\"></td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b>{{ _(\"Write off Amount\") }}</b>\n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;border-top: none;\">\n	<b>{{ doc.get_formatted(\"write_off_amount\") }} {{ companydoc.default_currency}}</b>\n	</td>\n	\n	</tr>\n	{%- endif -%}\n	<tr>\n	\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b><u>{{ _(\"Gross Amount Payable\") }}</u></b>\n	</td>\n	<td colspan=\"2\" class=\"text-right\" style=\"font-size:12px;border-top: none;\">\n	\n	<b><u>{{ doc.get_formatted(\"grand_total\") }} {{ companydoc.default_currency}}</u></b>\n	</td>\n	\n	</tr>\n	\n	\n	\n	<tr>\n	<td></td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b></b>\n	</td>\n	<td class=\"text-right\" style=\"font-size:12px;font-weight:bold;border-top: none;\">\n	\n	</td>\n	</tr>\n	<tr>\n	<td style=\"border: none;\"></td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b></b>\n	</td>\n	<td class=\"text-right\" style=\"font-size:12px;font-weight:bold;border-top: none;\">\n	</td>\n	</tr>	\n	<tr>\n	<td class=\"text-left\" style=\"width: 50%;font-size:12px;border-top: none;\">\n	<b>{{ doc.in_words }}</b>\n	</td>\n	<td class=\"text-right\" style=\"font-size:13px;font-weight:bold;border-top: none;\">\n	<b></b>\n	</td>\n	<td class=\"text-right\" style=\"font-size:12px;font-weight:bold;border-top: none;\">\n	</td>\n	</tr>\n	\n	</tbody>\n	</table>\n<br>\n<br>\n<br>\n<br>\n	<table style=\"width:100%\">\n  <tr>\n    <td style=\"text-align: center;\">______________</td>\n 	<td style=\"text-align: center;\">______________</td>\n    <td style=\"text-align: center;\">______________</td>\n  </tr>\n  <tr>\n    <td style=\"text-align: center;\">Prepared By</td>\n    <td style=\"text-align: center;\">Approved By</td>\n    <td style=\"text-align: center;\">Recived By</td>\n  </tr>\n</table>\n<hr>\n	<p>{{ doc.terms or \"\" }}</p>\n<p class=\"text-center\" style=\"font-size:11px\"><b>{{ _(\"This is system generated invoice, doesn\'t require stamp and signature.\") }}</b></p>\n<p class=\"text-center\" style=\"font-size:11px\"><b>{{ _(\"Thank you, please visit again.\") }}</b></p>\n\n<p class=\"text-center\" style=\"font-size:11px\"><b>{{ _(\"Powered by: www.uaepos.com\") }}</b></p>\n<script>\n	<script>\n	var currentdate = new Date(); \n	var datetime = currentdate.getDate() + \"/\"\n	+ (currentdate.getMonth()+1) + \"/\" \n	+ currentdate.getFullYear() + \" \" \n	+ currentdate.getHours() + \":\" \n	+ currentdate.getMinutes() + \":\" \n	+ currentdate.getSeconds();\n\n	$(\'.curr_date_time\').text(datetime);\n\n	</script>', NULL, NULL, NULL, 1, 'No', 0, NULL, 'Server', NULL, 'Sales Order', 0, 0);
INSERT INTO `tabPrint Format` VALUES ('Offer Letter', '2015-03-05 14:34:26.751210', '2015-12-04 11:46:28.897657', 'Administrator', 'Administrator', 0, NULL, NULL, NULL, 0, NULL, 0, 1, NULL, NULL, '{% set terms_exist = doc.terms and (doc.terms|striptags).strip() or \"\" %}\n\n{% if letter_head and not no_letterhead -%}\n    <div class=\"letter-head\">{{ letter_head }}</div>\n    <hr>\n{%- endif %}\n\n<div {% if terms_exist -%} class=\"page-break\" {%- endif %}>\n<p>\n\n<!-- offer letter content starts here, <br> is used to create new lines -->\nDate: {{ doc.offer_date }}\n<br><br>\n\nDear {{ doc.applicant_name }}, \n\n<br><br>\n\nWe are pleased to appoint you in the services of {{ doc.company }} on the terms and conditions detailed in this letter.\n\n<br><br>\n\nYour designation shall be <b>{{ doc.designation }}</b>.\n\n<br><br>\n\n<!-- offer letter terms and description from the table -->\n\n{%- if doc.offer_terms -%}\n    {%- for row in doc.offer_terms -%}\n        <b>{{ row.offer_term }}:</b> {{ row.value }}\n\n        <br>\n    {%- endfor -%}\n{%- endif -%}\n\n<br>\n\n<!-- offer letter content continues -->\n\n\nPlease read the detailed terms as below. If you have any queries, feel free to get in touch with us.\nWe look forward to your long and fruitful career association with our organisation.\nIf you decide to join us, \'Welcome to {{ doc.company }} !\'\n\n<br><br>\n\n<p class=\"strong\">\n\nYours truly,\n\n<br><br><br><br>\n\nAuthorized Signatory\n\n<br>\n\n{{ doc.company }}\n\n<!-- offer letter content ends here -->\n\n</p>\n</div>\n\n<!-- offer letter terms and conditions -->\n{% if terms_exist %}\n<div> {{ doc.terms }} </div>\n{% endif %}', NULL, NULL, NULL, 0, 'Yes', 0, NULL, 'Server', NULL, 'Offer Letter', 0, 0);
